    const [text, settext] = useState("");
    const textInput = useRef(null);
    useEffect(() => {
        console.log(text);
    }, [text])

    return (
        <div class="main">
            <div id="container"></div>
            <form id="input">
                <label for="textbox">Message : </label>
                <input type="text" ref={textInput} id="textbox"/>
                <input type="button" id="submit" onClick={()=>{settext(textInput.current.value)}}>SEND</input>
            </form>
        </div>
    )
}

export default Main
